<?php

require_once dirname(__FILE__) . '/../../Transmorph/Line.php';

/**
 * Test class for TransmorpLine.
 * Generated by PHPUnit on 2011-10-23 at 11:53:27.
 */
class Transmorph_LineTest extends PHPUnit_Framework_TestCase
{

    /**
     * @dataProvider constructorDataProvider
     *
     * @param string $source
     * @param string $target
     * @param string $line 
     */
    public function testConstructor($source, $target, $line)
    {
        $object = new Transmorph_Line($line);
        $this->assertEquals($source, $object->source);
        $this->assertEquals($target, $object->target);
    }

    /**
     * @dataProvider constructorExceptionsDataProvider
     * @expectedException Transmorph_Line_Exception
     *
     * @param string $line 
     */
    public function testConstructorExceptions($line)
    {
        new Transmorph_Line($line);
    }

    public function constructorDataProvider()
    {
        $data = array();

        $data[0] = array('a', 'b', 'a >> b');
        $data[1] = array('a a', 'b b', 'a a >> b b');
        $data[2] = array('a>>', '>>b', 'a>> >> >>b');
        $data[3] = array('a ', ' b', 'a  >>  b');
        $data[4] = array('', 'b', ' >> b');
        $data[5] = array('', '', ' >> ');
        $data[6] = array('a', '', 'a >> ');

        return $data;
    }

    public function constructorExceptionsDataProvider()
    {
        $data = array();

        $data[0] = array('a > b');
        $data[1] = array('a > > b');
        $data[2] = array('a>> b');
        $data[3] = array('a >>b');
        $data[4] = array('>> b');
        $data[5] = array('a >>');
        $data[6] = array('>>');
        $data[7] = array(' >>');
        $data[8] = array('>> ');
        
        return $data;
    }
    
    /**
     * @expectedException OutOfRangeException
     */
    public function testOutOfRange()
    {
        $object = new Transmorph_Line('x >> x');
        $object->unexisting;
    }

}